import java.util.Scanner;class sumof1darray{  public static void main (String ...x)  {	  int n,sum=0;		Scanner sc=new Scanner(System.in);		System.out.println("enter the value of  size of array");		n=sc.nextInt();		int a[]=new int[n];		System.out.println("enter the value of elements");		for(int i=0;i<a.length;i++)		{			a[i]=sc.nextInt();		}		for(int i=0;i<a.length;i++)		{			sum=sum+a[i];		}		System.out.println("sum  of araay is:"+sum);	}}