import java.util.Scanner;class array2d{  public static void main(String ...x)  {	  int m,n;		Scanner sc=new Scanner(System.in);		System.out.println("enter the value of  rows of array");		m=sc.nextInt();		System.out.println("enter the value of  columns of array");		n=sc.nextInt();		int a[][]=new int[m][n];		System.out.println("enetr the elements in the array");		for(int i=0;i<a.length;i++)		{			for(int j=0;j<a[i].length;j++)			a[i][j]=sc.nextInt();		}		System.out.println("elements in the array are");		for(int i=0;i<a.length;i++)		{			for(int j=0;j<a[i].length;j++)			{					System.out.print(a[i][j]);			}			System.out.println();		}			}}